'use strict';

const path = require('path');
const WifiDriver = require('./index.js');
const driver = new WifiDriver(path.basename(__dirname), {
	debug: true
});

driver.initDevice({id: 'dsf567bgb'}).then(() => {
	console.log(driver.getDevice({id: 'dsf567bgb'}));
	driver.deleteDevice({id: 'dsf567bgb'}).then(() => {
		console.log('device deleted')
		console.log(driver.getDevice({id: 'dsf567bgb'}));
	});
});

module.exports = driver;
